  --color-sidebar-accent: var(--sidebar-accent);
  --color-sidebar-accent-foreground: var(--sidebar-accent-foreground);
  --color-sidebar-border: var(--sidebar-border);
  --color-sidebar-ring: var(--sidebar-ring);
}

/* Enhanced animations and effects */
@keyframes float {
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-10px); }
}

@keyframes pulse-glow {
  0%, 100% { box-shadow: var(--glow-primary); }
  50% { box-shadow: var(--glow-primary), 0 0 30px rgba(212, 175, 55, 0.5); }
}

@keyframes gradient-shift {
  0% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
  100% { background-position: 0% 50%; }
}

@keyframes particle-float {
  0% { transform: translateY(0px) rotate(0deg); opacity: 0; }
  10% { opacity: 1; }
  90% { opacity: 1; }
  100% { transform: translateY(-100vh) rotate(360deg); opacity: 0; }
}

@keyframes shimmer {
  0% { background-position: -200% 0; }
  100% { background-position: 200% 0; }
}

@layer base {
  * {
    @apply border-border outline-ring/50;
  }
  body {
    @apply bg-background text-foreground;
  }

  h1,
  h2,
  h3,
  h4,
  h5,
  h6 {
    @apply text-balance;
  }
}

@layer utilities {
  /* Enhanced premium AI utilities */
  .text-gradient {
    background: var(--gradient-primary);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    background-size: 200% 200%;
    animation: gradient-shift 3s ease infinite;
  }

  .text-gradient-secondary {
    background: var(--gradient-secondary);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    background-size: 200% 200%;
    animation: gradient-shift 4s ease infinite;
  }

  .golden-border {
    border: 1px solid #d4af37;
    box-shadow: inset 0 0 20px rgba(212, 175, 55, 0.1);
  }

  .golden-glow {
    box-shadow: var(--glow-primary);
  }

  .luxury-card {
    @apply bg-card border border-border rounded-xl p-6 hover:border-accent/50 transition-all duration-300 hover:shadow-lg backdrop-blur-sm;
  }

  .luxury-card-premium {
    @apply bg-card border rounded-xl p-6 transition-all duration-300 hover:shadow-lg backdrop-blur-sm;
    background: linear-gradient(135deg, rgba(212, 175, 55, 0.1) 0%, rgba(244, 208, 63, 0.05) 50%, rgba(255, 215, 0, 0.1) 100%);
    border: 1px solid rgba(212, 175, 55, 0.3);
    box-shadow: var(--glow-primary);
  }

  .floating-element {
    animation: float 6s ease-in-out infinite;
  }

  .pulse-glow {
    animation: pulse-glow 2s ease-in-out infinite;
  }

  .gradient-bg {
    background: var(--gradient-primary);
    background-size: 400% 400%;
    animation: gradient-shift 8s ease infinite;
  }

  .shimmer-effect {
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.1), transparent);
    background-size: 200% 100%;
    animation: shimmer 2s infinite;
  }

  .glass-effect {
    background: rgba(255, 255, 255, 0.05);
    backdrop-filter: blur(10px);
    border: 1px solid rgba(255, 255, 255, 0.1);
  }

  .text-pretty-display {
    font-size: clamp(2.5rem, 8vw, 4rem);
    line-height: 1.1;
    font-weight: 700;
  }

  .text-pretty-subheading {
    font-size: clamp(1.125rem, 3vw, 1.5rem);
    line-height: 1.5;
    font-weight: 500;
    color: var(--color-muted-foreground);
  }

  .ai-glow {
    position: relative;
  }

  .ai-glow::before {
    content: '';
    position: absolute;
    top: -2px;
    left: -2px;
    right: -2px;
    bottom: -2px;
    background: var(--gradient-primary);
    border-radius: inherit;
    z-index: -1;
    opacity: 0;
    transition: opacity 0.3s ease;
  }

  .ai-glow:hover::before {
    opacity: 0.3;
  }

  .particle {
    position: absolute;
    width: 4px;
    height: 4px;
    background: var(--gradient-primary);
    border-radius: 50%;
    animation: particle-float 10s linear infinite;
  }

  .book-spine {
    background: linear-gradient(90deg, #8b4513 0%, #daa520 50%, #8b4513 100%);
    box-shadow: inset 0 0 10px rgba(0, 0, 0, 0.5);
  }

  .ai-circuit {
    background-image:
      radial-gradient(circle at 20% 80%, rgba(212, 175, 55, 0.3) 0%, transparent 50%),
      radial-gradient(circle at 80% 20%, rgba(102, 126, 234, 0.3) 0%, transparent 50%),
      radial-gradient(circle at 40% 40%, rgba(240, 147, 251, 0.3) 0%, transparent 50%);
    background-size: 100px 100px;
    animation: gradient-shift 20s ease infinite;
  }
}

